    public int canCompleteCircuit(int[] gas, int[] cost) {
        int left = 0 ;
        int start = -1;
        for(int i = 0; i < gas.length;i++){
            if(left + gas[i] >= cost[i]){
                if(start == -1){
                    start = i;
                }
                left += gas[i]-cost[i];
            }else{
                start = -1;
                left = 0;
            }
        }
        if(start == -1){
            return -1;
        }
        int k = 0;
        for(; k< start && left >= 0;k++){
            left += gas[k]-cost[k];
        }
        if(left < 0){
            start = -1;
        }
        return start;

    }
